const apiKey = 'MXoxYbU1E3NYet2NdscSkZiOglg8EcM4IfhsqVn3bvc2935EsszEztvunz3fnrQG4NBUzZXTwr7VHjU6eRt18JSRNLlOx3vmdF82nO6Zvkh85IB8aBxU16u1Sh5gXXYx';

const yelp = {
    searchYelp (term, location, soryBy) {
        return fetch(`https://cors-anywhere.herokuapp.com/https://api.yelp.com/v3/businesses/search?term=${term}&location=${location}&sort_by=${soryBy}`, 
    { headers: {
        authorization: `Bearer ${apiKey}`
            }
        }).then((response) => {
            return response.json();
        }).then((jsonResponse) => {
            if (jsonResponse.businesses) {
                return jsonResponse.businesses.map((business) => {
                    console.log(business);
                    return {
                        id: business.id,
                        imageSrc: bussiness.image_url,
                        name: business.name,
                        address: business.location.address1,
                        city: business.location.city,
                        state: business.location.state,
                        zipCode: business.location.zip_code,
                        category: business.categories[0].title,
                        rating: business.rating,
                        reviewCount: business.review_count,
                    };
                });
            }
        })
    }
};

export default yelp;
